import React from "react"
import {useParams} from "react-router-dom"
import OwlCarousel from 'react-owl-carousel';
import Mybreadcrumb from "../breadcrumb"
import Header from "../header"
import productsData from "./productsData"
import { Container } from "react-bootstrap";

function ProductDetail(prop) {
    const {productId} = useParams()
    const thisProduct = productsData.find(prod => prod.id === productId)

    return (
        <div className="page-wrapper">
            <Header isUserAthanticated={prop.isUserAthanticated} headerTheam="header headerWhite" logoAvtar="images/logoBlack.png" />
            <Mybreadcrumb />
            <Container>
                <OwlCarousel className='owl-simple' dots={false} loop items={3} margin={0} nav>

                </OwlCarousel>
            </Container>
            <h1>{thisProduct.name}</h1>
            <p>Price: ${thisProduct.price}</p>
            <p>{thisProduct.description}</p>
        </div>
    )
}

export default ProductDetail
