import React, { useEffect, useState, Component } from "react";
import {Tabs,Tab,Form,Button} from 'react-bootstrap'
import Checkbox from "react-custom-checkbox";
import * as Icon from "react-icons/fi";
import Header from "./header";
import Footer from "./footer";
import Mybreadcrumb  from "./breadcrumb";
var sectionStyle = {
	backgroundImage: "url(images/login-bg.jpg)"
  };
export default class Login extends React.Component{
	handleSubmit(event) {
		alert('A name was submitted: ' + this.input.current.value);
		event.preventDefault();
	}
	render(){
		const [email,setEmail]=useState("");
		const [password , setPassword]= useState("");
		useEffect(()=>{
			if(localStorage.getItem('user-info')){
				this.setState({isUserAthanticated: true}); 
			}
		},[])
		async function login() {
			console.warn(email,password);
			let item=(email , password);
			let result = await fetch("https://emart5432.herokuapp.com/login/" , {
				method: 'POST',
				headers: {
					"Content-Type": "application/json",
					"Accept": "application/json",
				},
				body: JSON.stringify(item)
			});
			result = await result.json();
			localStorage.setItem("user-info",JSON.stringify(result))
		}
		return(
			<div className='page-wrapper'>
				<Header isUserAthanticated={this.prop.isUserAthanticated} headerTheam="header"/>
				<Mybreadcrumb />
				<div className="login-page bg-image pt-8 pb-8 pt-md-12 pb-md-12 pt-lg-17 pb-lg-17" style={sectionStyle}>
					<div className="container">
						<div className="form-box">
							<div className="form-tab">
							<Tabs defaultActiveKey="signin" id="uncontrolled-tab-example" className=" nav-pills nav-fill">
									<Tab eventKey="signin" title="Sing In" className="nav-link">
										<form action="./home">
												<div class="form-group">
													<label for="singin-email">Username or email address *</label>
													<Form.Control className="form-control" type="email" onChange={(e)=>setEmail(e.target.value)} name="singin-email" id="singin-email" required/>
												</div>

												<div class="form-group">
													<label for="singin-password">Password *</label>
													<Form.Control className="form-control" type="password" onChange={(e)=>setPassword(e.target.value)} name="singin-password" id="singin-email" required/>
												</div>

												<div class="form-footer">
													<Button type="submit" onClick={login} className="btn btn-outline-primary-2">
														<span>LOG IN</span>
														<i class="icon-long-arrow-right"></i>
													</Button>

													<div className="myChackbox">
														<Checkbox
															icon={<Icon.FiCheck color="#174A41" size={14} />}
															name="my-input"
															checked={false}
															borderColor="#dadada"
															style={{ cursor: "pointer" }}
															labelStyle={{ marginLeft: 5, userSelect: "none" }}
															label="Remember Me"
														/>
													</div>

													<a href="#" class="forgot-link">Forgot Your Password?</a>
												</div>
											</form>
											<div class="form-choice">
												<p class="text-center">or sign in with</p>
												<div class="row">
													<div class="col-sm-6">
														<a href="#" class="btn btn-login btn-g">
															<i class="icon-google"></i>
															Login With Google
														</a>
													</div>
													<div class="col-sm-6">
														<a href="#" class="btn btn-login btn-f">
															<i class="icon-facebook-f"></i>
															Login With Facebook
														</a>
													</div>
												</div>
											</div>
									</Tab>
									<Tab eventKey="register" title="Register" className="nav-link">
									<form action="#">
												<div class="form-group">
													<label for="register-email-2">Your email address *</label>
													<input type="email" class="form-control" id="register-email-2" name="register-email" required />
												</div>

												<div class="form-group">
													<label for="register-password-2">Password *</label>
													<input type="password" class="form-control" id="register-password-2" name="register-password" required />
												</div>

												<div class="form-footer">
													<button type="submit" class="btn btn-outline-primary-2">
														<span>SIGN UP</span>
														<i class="icon-long-arrow-right"></i>
													</button>
													<Checkbox
														icon={<Icon.FiCheck color="#174A41" size={14} />}
														name="my-input"
														checked={false}
														borderColor="#dadada"
														style={{ cursor: "pointer" }}
														labelStyle={{ marginLeft: 5, userSelect: "none" }}
														label="I agree to the"
													/>
												</div>
											</form>
											<div class="form-choice">
												<p class="text-center">or sign in with</p>
												<div class="row">
													<div class="col-sm-6">
														<a href="#" class="btn btn-login btn-g">
															<i class="icon-google"></i>
															Login With Google
														</a>
													</div>
													<div class="col-sm-6">
														<a href="#" class="btn btn-login  btn-f">
															<i class="icon-facebook-f"></i>
															Login With Facebook
														</a>
													</div>
												</div>
											</div>
									</Tab>
								</Tabs>
							</div>
						</div>
					</div>
				</div>
				<Footer />
			</div>
		);
	}   
}
