import { useState } from "react";
import { Addtocard } from "../api/product/card";
import { FetchCardData, GetAuthDetail } from "./utils";

export default function Quantity(prop){
	const dispatch = useDispatch();
    const [value, setvalue] = useState(prop.value);
    const userAthantication = GetAuthDetail();
    async function increment(id) {
        setvalue(value+1)
        const cardData = {
            "product": id,
            "quantity": value,
          }
        const cardApi = await Addtocard(cardData,userAthantication);
        // this.setState(prevState => {value: ++prevState.value});
      }

    async function decrement(id) {
        setvalue(value > 1? value-1 : 1)
        const cardData = {
            "product": id,
            "quantity": value,
          }
        const cardApi = await Addtocard(cardData,userAthantication);
        if(cardApi.statusCode===201||cardApi.statusCode===200){
            FetchCardData(dispatch)
        }
        // this.setState(prevState => {value: prevState.value > 0? --prevState.value : 0});
    }
    return(
        <div className="quantity-input">
            {value>1?<button className="quantity-input__modifier quantity-input__modifier--left" onClick={()=> decrement()}>
                &mdash;
            </button>:<button className="quantity-input__modifier quantity-input__modifier--left" disabled>
                &mdash;
            </button>}
            <input className="quantity-input__screen" type="text" value={value} readonly min={1}/>
            <button className="quantity-input__modifier quantity-input__modifier--right" onClick={()=> increment()}>
                &#xff0b;
            </button>  
        </div> 
    );
}
