                                                            <h4 className="product-title">
                                                                <a href="#">{card.product.product_name}</a>
                                                            </h4>

                                                            <span className="cart-product-info">
                                                                <span className="cart-product-qty">{card.quantity}</span>
                                                                x ${card.product.price}
                                                            </span>
                                                        </div>

