                                        <a href="#" className="btn btn-primary">View Cart</a>
                                        <a href="#" className="btn btn-outline-primary-2"><span>Checkout</span><i className="icon-long-arrow-right"></i></a>
                                    </div>
                                </div>
                            </div>
        </>
    )
