import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import Home from '../Components/home';
import Login from '../Components/login';
import ProductDetail from '../Components/product/ProductDetail';
import ProductPage from '../Components/product/productPage';
class App extends React.Component {
  constructor(props){  
    super(props);  
    this.state = {  
      isUserAthanticated: true,
      items: [],
      DataisLoaded: false
      // clicks: 0,
      // show: 1
    }
  }
  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
    .then((res) => res.json())
    .then((json) => {
      this.setState({
        items: json,
        DataisLoaded: false
      });
    })
  }
  // addTocard = () => {
  //   this.setState({ clicks: this.state.clicks + 1 });
  // }
  render(){
    const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Pleses wait some time.... </h1> </div> ;
    return (
      <div className = "App">
            <h1> Fetch data from an api in react </h1>  {
                items.map((item) => ( 
                <ol key = { item.id } >
                    User_Name: { item.username }, 
                    Full_Name: { item.name }, 
                    User_Email: { item.email } 
                    </ol>
                ))
            }
        </div>
      // <Router>
      //   <Routes>
      //     <Route exact path="/" element={<Home isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     <Route exact path="/shop" element={<Home isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     <Route exact path="/products" element={<ProductPage isUserAthanticated={this.state.isUserAthanticated}/>}/>
      //     <Route exact path="/products/:productId" element={<ProductDetail isUserAthanticated={this.state.isUserAthanticated}/>}/>
      //     <Route exact path="/pages" element={<Home isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     <Route exact path="/blog" element={<Home isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     <Route exact path="/elements" element={<Home isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     <Route path="/login" element={ <Login isUserAthanticated={this.state.isUserAthanticated}/>} />
      //     {/* <Navigate to="/" element={<Home />} /> */}
      //   </Routes>
      // </Router>
    );
  }
}

export default App;
