import React from "react";
import { Link } from "react-router-dom";
import productsData from "./productsData";

export default function Products(prop) {
  addTocard = () => {
    this.setState({ clicks: this.state.clicks + 1 });
  }
  const products = productsData.map(product => {
    return (
      <div className={prop.grid}>
        <div key={product.id} className="product product-10 text-center">
          <figure className="product-media">
            <Link to={`/products/${product.id}`}>
              <img src={product.img1} alt={product.name} className="product-image" />
              <img src={product.img2} alt={product.name} className="product-image-hover" />
            </Link>
          </figure>
          <div className="product-body">
            <div className="product-action">
              <a href="#" className="btn-cart" onClick={this.addTocard}><span>add to cart</span></a>
              <a href="#" className="btn-product-icon btn-wishlist"><span>Add to Wishlist</span></a>
            </div>
            <div className="product-intro">
              <h3 className="product-title">
                <Link to={`/products/${product.id}`}>
                  {product.name}
                </Link>
              </h3>
              <div className="product-price">${product.price}</div>
            </div>
            <div className="product-detail">
              <div className="ratings-container">
                <div className="ratings">
                  <div className="ratings-val"></div>
                </div>
                <span className="ratings-text">( 6 Reviews )</span>
              </div>             
            </div>
          </div>
        </div>
      </div>
      // <div key={product.id}>
      //   <h3>
      //     <Link to={`/products/${product.id}`}>{product.name}</Link>
      //   </h3>
      //   <p>Price: ${product.price}</p>
      //   <hr />
      // </div>
    );
  });
  return(
    <div className="row">
      {products}
    </div>
  );
}
